using FluentNHibernate.Mapping;using Ovi.Task.Data.Entity;namespace Ovi.Task.Data.Mapping{    public sealed class TMCANCELLATIONREASONS_MAP : ClassMap<TMCANCELLATIONREASONS>    {        public TMCANCELLATIONREASONS_MAP()        {            CompositeId()                .KeyProperty(x => x.CNR_CODE)                .KeyProperty(x => x.CNR_ENTITY);            Map(x => x.CNR_DESC);            Map(x => x.CNR_DESCF).Formula("dbo.GetDesc('TMCANCELLATIONREASONS','CNR_DESC', CNR_ENTITY + '#' + CNR_CODE , CNR_DESC, :SessionContext.Language)");            Map(x => x.CNR_ACTIVE);            Map(x => x.CNR_CREATED);            Map(x => x.CNR_CREATEDBY);            Map(x => x.CNR_UPDATED);            Map(x => x.CNR_UPDATEDBY);            Map(x => x.CNR_RECORDVERSION).Default("0");            Map(x => x.CNR_SQLIDENTITY).Generated.Insert();        }    }}