using FluentNHibernate.Mapping;using Ovi.Task.Data.Configuration;using Ovi.Task.Data.Entity;namespace Ovi.Task.Data.Mapping{    public sealed class TMCUSTOMERGROUPS_MAP : ClassMap<TMCUSTOMERGROUPS>    {        public TMCUSTOMERGROUPS_MAP()        {            Id(x => x.CUG_CODE).Length(PropertySettings.L50);            Map(x => x.CUG_DESC).Length(PropertySettings.L250);            Map(x => x.CUG_ACTIVE);            Map(x => x.CUG_CREATED);            Map(x => x.CUG_CREATEDBY).Length(PropertySettings.L50);            Map(x => x.CUG_UPDATED);            Map(x => x.CUG_UPDATEDBY).Length(PropertySettings.L50);            Map(x => x.CUG_SQLIDENTITY).Generated.Insert();            Map(x => x.CUG_RECORDVERSION).Default("0");        }    }}