using FluentNHibernate.Mapping;using Ovi.Task.Data.Entity;namespace Ovi.Task.Data.Mapping{    public sealed class TMHOLDREASONHISTORYVIEW_MAP : ClassMap<TMHOLDREASONHISTORYVIEW>    {        public TMHOLDREASONHISTORYVIEW_MAP()        {            ReadOnly();            CompositeId()                .KeyProperty(x => x.TSK_ID)                .KeyProperty(x => x.TSK_HOLDORDER);            Map(x => x.TSK_HOLDREASON);            Map(x => x.TSK_HOLDREASONDESC).ReadOnly().Formula("dbo.GetDesc('TMHOLDREASONS','HDR_DESC', TSK_HOLDREASON, (SELECT h.HDR_DESC FROM TMHOLDREASONS h WHERE h.HDR_CODE = TSK_HOLDREASON),:SessionContext.Language)");            Map(x => x.TSK_HOLDDURATION);            Map(x => x.TSK_UPDATEDBY);        }    }}