using FluentNHibernate.Mapping;using Ovi.Task.Data.Configuration;using Ovi.Task.Data.Entity;namespace Ovi.Task.Data.Mapping{    public sealed class TMHOLIDAYS_MAP : ClassMap<TMHOLIDAYS>    {        public TMHOLIDAYS_MAP()        {            Id(x => x.HOL_ID);            Map(x => x.HOL_DESC).Length(PropertySettings.L250);            Map(x => x.HOL_TYPE).Length(PropertySettings.L50);            Map(x => x.HOL_DATE);            Map(x => x.HOL_ALLDAY);            Map(x => x.HOL_START);            Map(x => x.HOL_END);            Map(x => x.HOL_CREATED);            Map(x => x.HOL_CREATEDBY).Length(PropertySettings.L50);            Map(x => x.HOL_UPDATED);            Map(x => x.HOL_UPDATEDBY).Length(PropertySettings.L50);            Map(x => x.HOL_RECORDVERSION).Default("0");        }    }}