using FluentNHibernate.Mapping;using Ovi.Task.Data.Configuration;using Ovi.Task.Data.Entity;namespace Ovi.Task.Data.Mapping{    public sealed class TMHOLIDAYTYPES_MAP : ClassMap<TMHOLIDAYTYPES>    {        public TMHOLIDAYTYPES_MAP()        {            Id(x => x.HOT_CODE).Length(PropertySettings.L50);            Map(x => x.HOT_DESC).Length(PropertySettings.L250);            Map(x => x.HOT_DESCF).Formula("dbo.GetDesc('TMHOLIDAYTYPES','HOT_DESC', HOT_CODE, HOT_DESC,:SessionContext.Language)");
            Map(x => x.HOT_ACTIVE);            Map(x => x.HOT_CREATED);            Map(x => x.HOT_CREATEDBY).Length(PropertySettings.L50);            Map(x => x.HOT_UPDATED);            Map(x => x.HOT_UPDATEDBY).Length(PropertySettings.L50);            Map(x => x.HOT_RECORDVERSION).Default("0");            Map(x => x.HOT_SQLIDENTITY).Generated.Insert();        }    }}