using FluentNHibernate.Mapping;using Ovi.Task.Data.Entity;namespace Ovi.Task.Data.Mapping{    public sealed class TMLEAVEDURATIONS_MAP : ClassMap<TMLEAVEDURATIONS>    {        public TMLEAVEDURATIONS_MAP()        {            Id(x => x.LDU_ID);            Map(x => x.LDU_TYPE);            Map(x => x.LDU_TYPEENTITY);            Map(x => x.LDU_TIME);            Map(x => x.LDU_CREATED);            Map(x => x.LDU_CREATEDBY);            Map(x => x.LDU_UPDATED);            Map(x => x.LDU_UPDATEDBY);            Map(x => x.LDU_RECORDVERSION);        }    }}