using FluentNHibernate.Mapping;using Ovi.Task.Data.Configuration;using Ovi.Task.Data.Entity;namespace Ovi.Task.Data.Mapping{    public sealed class TMPRICINGCODEPARAMETERS_MAP : ClassMap<TMPRICINGCODEPARAMETERS>    {        public TMPRICINGCODEPARAMETERS_MAP()        {            Id(x => x.PCP_ID);            Map(x => x.PCP_PRICINGCODE).Length(PropertySettings.L50);            Map(x => x.PCP_CURR).Length(PropertySettings.L50);            Map(x => x.PCP_CURRDESC).Formula("dbo.GetDesc('TMCURRENCIES','CUR_DESC', PCP_CURR, (SELECT c.CUR_DESC FROM TMCURRENCIES c WHERE c.CUR_CODE = PCP_CURR), :SessionContext.Language)");            Map(x => x.PCP_PRICE);            Map(x => x.PCP_STARTDATE);            Map(x => x.PCP_ENDDATE);            Map(x => x.PCP_CREATED);            Map(x => x.PCP_CREATEDBY).Length(PropertySettings.L50);            Map(x => x.PCP_UPDATED);            Map(x => x.PCP_UPDATEDBY).Length(PropertySettings.L50);            Map(x => x.PCP_RECORDVERSION);        }    }}