using FluentNHibernate.Mapping;using Ovi.Task.Data.Configuration;using Ovi.Task.Data.Entity;namespace Ovi.Task.Data.Mapping{    public sealed class TMPRICINGCODES_MAP : ClassMap<TMPRICINGCODES>    {        public TMPRICINGCODES_MAP()        {            Id(x => x.PRC_CODE).Length(PropertySettings.L50);            Map(x => x.PRC_DESC).Length(PropertySettings.L250);            Map(x => x.PRC_GROUP).Length(PropertySettings.L50);            Map(x => x.PRC_ACTIVE);            Map(x => x.PRC_CREATED);            Map(x => x.PRC_CREATEDBY).Length(PropertySettings.L50);            Map(x => x.PRC_UPDATED);            Map(x => x.PRC_UPDATEDBY).Length(PropertySettings.L50);            Map(x => x.PRC_SQLIDENTITY).Generated.Insert();            Map(x => x.PRC_RECORDVERSION).Default("0");        }    }}