using FluentNHibernate.Mapping;using Ovi.Task.Data.Configuration;using Ovi.Task.Data.Entity;namespace Ovi.Task.Data.Mapping{    public sealed class TMSECURITYFILTERS_MAP : ClassMap<TMSECURITYFILTERS>    {        public TMSECURITYFILTERS_MAP()        {            Id(x => x.SCF_CODE).Length(PropertySettings.L50);            Map(x => x.SCF_DESC).Length(PropertySettings.L250);            Map(x => x.SCF_SCREEN).Length(PropertySettings.L50);            Map(x => x.SCF_SCREENDESCF).Formula("dbo.GetDesc('TMSCREENS','SCR_DESC', SCF_SCREEN, (SELECT s.SCR_DESC FROM TMSCREENS s WHERE s.SCR_CODE = SCF_SCREEN),:SessionContext.Language)");            Map(x => x.SCF_CONDITION).Length(PropertySettings.L4001);            Map(x => x.SCF_COLUMNFILTER).Length(PropertySettings.L500);            Map(x => x.SCF_ACTIVE);            Map(x => x.SCF_ISVALIDATED);            Map(x => x.SCF_CREATED);            Map(x => x.SCF_CREATEDBY).Length(PropertySettings.L50);            Map(x => x.SCF_UPDATED);            Map(x => x.SCF_UPDATEDBY).Length(PropertySettings.L50);            Map(x => x.SCF_RECORDVERSION).Default("0");            Map(x => x.SCF_SQLIDENTITY).Generated.Insert();        }    }}