using FluentNHibernate.Mapping;using Ovi.Task.Data.Configuration;using Ovi.Task.Data.Entity;namespace Ovi.Task.Data.Mapping{    public sealed class TMSHIFTS_MAP : ClassMap<TMSHIFTS>    {        public TMSHIFTS_MAP()        {            Id(x => x.SHF_CODE).Length(PropertySettings.L50);            Map(x => x.SHF_DESCRIPTION).Length(PropertySettings.L250);            Map(x => x.SHF_ORGANIZATION).Length(PropertySettings.L50);            Map(x => x.SHF_ORGANIZATIONDESC).Formula("dbo.GetDesc('TMORGS','ORG_DESC', SHF_ORGANIZATION,(SELECT o.ORG_DESC FROM TMORGS o WHERE o.ORG_CODE = SHF_ORGANIZATION),:SessionContext.Language)");            Map(x => x.SHF_STARTTIME1);            Map(x => x.SHF_DURATION1);            Map(x => x.SHF_STARTTIME2);            Map(x => x.SHF_DURATION2);            Map(x => x.SHF_ACTIVE);            Map(x => x.SHF_CREATED);            Map(x => x.SHF_CREATEDBY).Length(PropertySettings.L50);            Map(x => x.SHF_UPDATED);            Map(x => x.SHF_UPDATEDBY).Length(PropertySettings.L50);            Map(x => x.SHF_RECORDVERSION).Default("0");            Map(x => x.SHF_SQLIDENTITY).Generated.Insert();        }    }}