using FluentNHibernate.Mapping;using Ovi.Task.Data.Entity;namespace Ovi.Task.Data.Mapping{    public sealed class TMTIMEKEEPINGLINESVIEW_MAP : ClassMap<TMTIMEKEEPINGLINESVIEW>    {        public TMTIMEKEEPINGLINESVIEW_MAP()        {            ReadOnly();            CompositeId()                .KeyProperty(x => x.TKD_TIMEKEEPING)                .KeyProperty(x => x.TKD_USER)                .KeyProperty(x => x.TKD_DATE);            Map(x => x.TKD_TIMEKEEPINGDESC);            Map(x => x.TKD_USERDESC);            Map(x => x.TKD_SHIFT);            Map(x => x.TKD_EXCEPTION);            Map(x => x.TKD_MINSTART);            Map(x => x.TKD_MAXEND);            Map(x => x.TKD_DIFF);            Map(x => x.TKD_SUM);            Map(x => x.TKD_TIMEONROUTE);            Map(x => x.TKD_NORMAL);            Map(x => x.TKD_OVERTIME);            Map(x => x.TKD_STATUS);            Map(x => x.TKD_APPROVALLINE);            Map(x => x.TKD_APPROVER);            Map(x => x.TKD_APPROVERDESC);        }    }}