using FluentNHibernate.Mapping;using Ovi.Task.Data.Entity;namespace Ovi.Task.Data.Mapping{    public sealed class TMUSERGROUPHOLDREASONS_MAP : ClassMap<TMUSERGROUPHOLDREASONS>    {        public TMUSERGROUPHOLDREASONS_MAP()        {            Id(x => x.UGH_ID);            Map(x => x.UGH_USERGROUP);            Map(x => x.UGH_HOLDREASON);            Map(x => x.UGH_CREATED);            Map(x => x.UGH_CREATEDBY);            Map(x => x.UGH_RECORDVERSION);        }    }}