using FluentNHibernate.Mapping;using Ovi.Task.Data.Configuration;using Ovi.Task.Data.Entity;namespace Ovi.Task.Data.Mapping{    public sealed class TMUSERSHIFTS_MAP : ClassMap<TMUSERSHIFTS>    {        public TMUSERSHIFTS_MAP()        {            Id(x => x.USH_ID);            Map(x => x.USH_USER).Length(PropertySettings.L50);            Map(x => x.USH_SHIFT).Length(PropertySettings.L50);            Map(x => x.USH_SHIFTDESC).ReadOnly().Formula("(SELECT s.SHF_DESCRIPTION FROM TMSHIFTS s WHERE s.SHF_CODE = USH_SHIFT)");            Map(x => x.USH_DATE);            Map(x => x.USH_CREATED);            Map(x => x.USH_CREATEDBY).Length(PropertySettings.L50);        }    }}